import './App.css';
import { useState } from 'react';


function App() {
  // Using hooks to manipulate the position of the block
  // Can be visualized in a 2D-Plane, clicking buttons will change its X and Y position
  const [xPosition, setxPosition] = useState(0)
  const [yPosition, setyPosition] = useState(0)

  return (
    <div className="App">
      <div className='parent-container'>
        <button className='horizontal' onClick={() => setyPosition(yPosition - 50)} disabled={yPosition === 0}>Up</button>
        <div className='container'>
          <button className='vertical' onClick={() => setxPosition(xPosition - 50)} disabled={xPosition === 0}> Left</button>
          <div className='main'>
            <div className='box' style={{ left: xPosition, top: yPosition }}></div>
            </div>
            <button className='vertical' onClick={() => setxPosition(xPosition + 50)} disabled={xPosition === 450}>Right</button>
          </div>
          <button className='horizontal' onClick={() => setyPosition(yPosition + 50)} disabled={yPosition === 450}>Bottom</button>
        </div>
    </div>
  );
}

export default App;
